mapper.select:
select * from
	(select ROWNUM RNUM, notice_no, notice_title, notice_content, notice_date, notice_center , notice_writer
	from (select * from BLOG_NOTICE WHERE notice_writer = #{notice_writer} order by notice_date desc))
where RNUM >=#{startrow} and RNUM <=#{endrow}

mapper.insert:
INSERT INTO BLOG_NOTICE (
notice_no		
, notice_title
, notice_date
, notice_writer
, notice_view
, notice_content
, notice_email
, notice_password
, notice_center

) VALUES (

blog_notice_seq.nextval		
, #{notice_title}
, sysdate
, #{notice_writer}
, #{notice_view}
, #{notice_content}
, #{notice_email}
, #{notice_password}
, #{notice_center}


)

INSERT INTO CALENDAR (
title 
, start_date
, end_date		
) VALUES (		
#{title}
,#{start}
,#{end}		
)

mapper.update:
UPDATE BLOG_NOTICE
SET notice_title =  #{notice_title}
, notice_content = #{notice_content}			
WHERE notice_no = #{notice_no}
