mapper.select:
select * from (select rownum rnum , center_name , center_add , center_tel from center_location cl where 1=1 and cl.center_name not in ( select cu.center_name from center_user cu )
and center_name like '%'||#{center_name}||'%'
) where rnum >=#{startrow} and rnum <=#{endrow}

mapper.insert:
Insert into center_user
(center_id
, password
, last_pass_change_dt
, info_agrement_yn
, info_agrement_dt
, registration_dt
, id_lock
, center_tel
, center_email
, center_add
, center_name
, center_user_name
, center_domain
) values (
 #{center_id}
 , #{password}
 , sysdate
 , 'Y'
 , sysdate
 , sysdate
 , 'UNLOCK'
 , #{center_tel}
 , #{center_email}
 , #{center_add}
 , #{center_name}
 , #{center_user_name}
 , #{center_id}	
)
